<template>
  <div>
    <div class="d-flex justify-content-center align-items-center">
      <div class="p-3">
        <img :src="dataUrl" class="imagen_producto" />
      </div>
      <div class="p-2">
        <h5>Producto:</h5>
        <p>{{ articulo.nombre }}</p>
        <h5>Descripci√≥n:</h5>
        <p>{{ articulo.descripcion }}</p>
      </div>
    </div>
    <div class="d-flex container-fluid justify-content-end pb-2 mt-n2">
      <a
        :href="articulo.url"
        class="btn btn-outline-info"
        role="button"
        aria-pressed="true"
        target="_blank"
        >Info</a
      >
    </div>
  </div>
</template>

<script>
export default {
  name: "ArticuloData",
  props: {
    articulo: Object,
  },
  computed: {
    dataUrl() {
      if (this.articulo.imagen != null) {
        return (
          "data:image/png;base64," +
          btoa(
            new Uint8Array(this.articulo.imagen.data).reduce(
              (data, byte) => data + String.fromCharCode(byte),
              ""
            )
          )
        );
      } else {
        return (
          "data:image/jpeg;base64," +
          ""
        );
      }
    },
  },
};
</script>

